import entities.Tariffs;import javax.xml.bind.JAXBContext;import javax.xml.bind.JAXBException;import javax.xml.bind.Marshaller;import javax.xml.bind.Unmarshaller;import java.io.File;import java.io.IOException;import java.net.URL;public class XmlToObject {    public static void main(String[] args) {        try {            String fileNameFrom = "xml/tariff.xml";            URL url = ClassLoader.getSystemResource(fileNameFrom);            JAXBContext jaxbContext = JAXBContext.newInstance(Tariffs.class);            Unmarshaller jaxbUnmarshaller = jaxbContext.createUnmarshaller();            Tariffs tariffs= (Tariffs) jaxbUnmarshaller.unmarshal(url);            System.out.println(tariffs.toString());            String fileNameTo = "src/main/resources/xml/tariff_result.xml";            File fileTo = new File(fileNameTo);            fileTo.createNewFile();            Marshaller jaxbMarshaller = jaxbContext.createMarshaller();            jaxbMarshaller.marshal(tariffs, fileTo);        } catch (JAXBException | IOException e) {            e.printStackTrace();        }    }}